styles:
:host{
  display: block;
  float: left;
  width: 100%;
}
.doc-breadcrumb-container {
    padding-left: 14px;
    display: inline-block;
    vertical-align: middle;
    margin-top: 6px; }

  .doc-breadcrumb-container .doc-breadcrumb {
    margin-bottom: 0;
    color: #999; }

  @media (max-width: 1440px) {
    .doc-breadcrumb-container .doc-breadcrumb {
      overflow: hidden;
      white-space: nowrap;
      text-overflow: ellipsis; } }
  .doc-breadcrumb {
    height: 32px; }

  .doc-breadcrumb .doc-breadcrumb__li {
    float: left;
    cursor: pointer;
    line-height: 32px;
    padding-right: 5px; }
    .doc-breadcrumb .doc-breadcrumb__li a {
      color: #337ab7; }

  .doc-breadcrumb__li .doc-breadcrumb__li__click--first {
    font-weight: 900;
    cursor: default;
    color: #000000 !important; }
    .doc-breadcrumb__li .doc-breadcrumb__li__click--first a {
      cursor: default;
      color: #000000; }
    .doc-breadcrumb__li .doc-breadcrumb__li__click--first .breadcrumb--right--iocn {
      font-weight: normal; }

  .doc-breadcrumb .doc-breadcrumb__li.last {
    cursor: default; }

  .doc-breadcrumb .doc-breadcrumb__li.last a {
    color: #000000;
    cursor: default; }

  .doc-breadcrumb .doc-breadcrumb__li.last .doc-breadcrumb__li__rgticon {
    visibility: hidden; }

  .doc-breadcrumb a {
    color: #000000; }

  .doc-breadcrumb__li__click__title {
    display: inline-block;
    max-width: 150px;
    height: 15px;
    line-height: 15px;
    vertical-align: text-bottom;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis; }

  .fa-angle-right {
    font-size: 14px;
    color: #999; }

  .curSelectedNode {
    color: red !important; }

